# Group size for decoding (e.g., 8 bits for a byte)
GROUP_SIZE = 8

# Function to group bits
def group_bits(data, group_size):
    return [data[i:i+group_size] for i in range(0, len(data), group_size)]

# Function to convert binary to integers
def binary_to_int(binary):
    return int("".join(map(str, binary)), 2)

# Process the data
grouped_data = group_bits(data, GROUP_SIZE)
decoded_values = [binary_to_int(group) for group in grouped_data if len(group) == GROUP_SIZE]

# Display results
print("Grouped Data:")
for group in grouped_data:
    print(group)

print("\nDecoded Values:")
print(decoded_values)
IDLE_VALUE = 0  # Define what represents idle (e.g., 0 or a specific range)
SEGMENT_THRESHOLD = 5  # Minimum length of a segment to be considered "active"

# Function to segment active regions
def segment_active_data(data, idle_value, segment_threshold):
    active_segments = []
    current_segment = []

    for value in data:
        if value != idle_value:  # Detect non-idle value
            current_segment.append(value)
        else:
            if len(current_segment) >= segment_threshold:
                active_segments.append(current_segment)
            current_segment = []  # Reset current segment

    # Append the last segment if it's valid
    if len(current_segment) >= segment_threshold:
        active_segments.append(current_segment)

    return active_segments

# Process the data
active_segments = segment_active_data(decoded_values, IDLE_VALUE, SEGMENT_THRESHOLD)

# Display results
print("Active Segments:")
for i, segment in enumerate(active_segments, start=1):
    print(f"Segment {i}: {segment}")


def decode_rgb_segments(segments):
    decoded_colors = []
    for segment in segments:
        if len(segment) % 3 == 0:  # Ensure divisible by 3 for RGB
            rgb_values = [segment[i:i+3] for i in range(0, len(segment), 3)]
            decoded_colors.extend(rgb_values)
    return decoded_colors

# Decode RGB data
decoded_colors = decode_rgb_segments(active_segments)
print("Decoded RGB Colors:", decoded_colors)
